import Link from 'next/link'
import { useState, useEffect } from 'react'
import './navbar.scss'
import {useStoreActions, useStoreState} from 'easy-peasy'

const Navbar = () => {


  const [active, setActive] = useState('home')
  const menu = ['HOME', 'ABOUT', 'SERVICES', 'CONTACT', 'CLIENT']

  const changeColor = (x)=>{
    if(x == active){
      return 'orange'
    }else{
      return 'none'
    }
  }

  useEffect(()=>{
    console.log(active);



  }, [changeColor])

  return (
    <>
        <main className='sidebar'>

          {menu.map((e, i)=>{
            e = e.toLowerCase()
            return (
              <Link href={e == 'home' ? `/` : `/${e}`} key={i} >
              <div className="menuItem" onClick={()=>{
                setActive(e)
              }} style={{
                backgroundColor : changeColor(e),
              }}>
                <a>{e}</a>
              </div>
              </Link>
            )
          })}


        </main>
    </>
  )
}

export default Navbar
