import {useStoreState, useStoreActions} from 'easy-peasy'
import { useEffect, useState } from 'react'


const Hero = () => {


  const nav = useStoreState(state => state.isActive)
  const setNav = useStoreActions(action => action.setActive)
  const username = useStoreState(state => state.username)
  const [active, setSetActive] = useState(false)

  useEffect(function(){
    console.log(active);
    setSetActive(!active)

    // window.location.href = '/about'
  },[])


  return (
    <div className='hero'>

        <h1>{username}</h1>
        <button onClick={()=>{
          setSetActive(true)
        }}>togle</button>


    </div>
  )
}

export default Hero
