import {useStoreState, useStoreActions} from 'easy-peasy'
import { useEffect } from 'react'


const Hero = () => {


  const nav = useStoreState(state => state.isActive)
  const setNav = useStoreActions(action => action.setActive)
  const username = useStoreState(state => state.username)

  const isLoaded = ()=>{
    window.location.href = '/about'
  }

  useEffect(function(){
    console.log('page about loaded');
    // window.location.href = '/about'
    isLoaded
  },[setNav])


  return (
    <div className='hero'>

        <h1>{username}</h1>
        <button onClick={()=>{
          setNav()
        }}>togle</button>


    </div>
  )
}

export default Hero
