import Link from 'next/link'
import { useState, useEffect } from 'react'
import './navbar.scss'
import {useStoreActions, useStoreState} from 'easy-peasy'

const Navbar = () => {


  const [active, setActive] = useState()
  const menu = ['HOME', 'ABOUT', 'SERVICES', 'CONTACT', 'CLIENT']
  const navActive = useStoreState(state => state.isActive) 
  const setNavActive = useStoreActions(action => action.setActive)

  useEffect(()=>{
    console.log(navActive);

  }, [])

  return (
    <>
        <main className='sidebar'>

          {menu.map((e, i)=>{
            e = e.toLowerCase()
            return (
              <Link href={e == 'home' ? `/` : `/${e}`} key={i} >
              <div className="menuItem" id={e} style={{
                backgroundColor : navActive == e ? 'orange' : 'white',
              }} onClick={()=>{
                setActive('')
                setNavActive(e)
              }}>
                <a>{e}</a>
              </div>
              </Link>
            )
          })}


        </main>
    </>
  )
}

export default Navbar
