import Link from 'next/link'
import { useState, useEffect } from 'react'
import './navbar.scss'

const Navbar = () => {


  const [active, setActive] = useState()
  const menu = ['HOME', 'ABOUT', 'SERVICES', 'CONTACT', 'CLIENT']


  useEffect(()=>{
    console.log(active);

  }, [active])

  return (
    <>
        <main className='sidebar'>

          {menu.map((e, i)=>{
            e = e.toLowerCase()
            return (
              <Link href={e == 'home' ? `/` : `/${e}`} key={i} >
                <div className="menuItem" id={e} onClick={()=>{
                  setActive(e)
                }} style={{
                  color : setActive == e ? 'orange' : 'gray',
                }}>
                  <a>{e}</a>
                </div>
              </Link>
            )
          })}


        </main>
    </>
  )
}

export default Navbar
