import Link from 'next/link'
import { useState, useEffect } from 'react'
import './navbar.scss'
import {createStore, StoreProvider, useStoreActions} from 'easy-peasy'
import Dict from '../../../model/Dict'

const Navbar = () => {

  const store = createStore(Dict)
  const sa = useStoreActions(action => action.setActive)
  const menu = ['HOME', 'ABOUT', 'SERVICES', 'CONTACT', 'CLIENT']


  // useEffect(()=>{
  //   console.log(active);

  // }, [active])

  return (
    <>
        <StoreProvider store={store}>
        <main className='sidebar'>

          {menu.map((e, i)=>{
            e = e.toLowerCase()
            return (
              <Link href={e == 'home' ? `/` : `/${e}`} key={i} >
                <div className="menuItem" id={e} onClick={()=>{
                  sa(e)
                }} >
                  <a style={{
                  color : active == e ? 'orange' : 'gray',
                }}>{e}</a>
                </div>
              </Link>
            )
          })}


        </main>
        </StoreProvider>
    </>
  )
}

export default Navbar
