import React from 'react'
import {useStoreActions, useStoreState} from 'easy-peasy'

const SideBar = () => {
    const sa = useStoreActions(action => action.setActive)
    const active = useStoreState(state => state.isActive)
    const menu = ['HOME', 'ABOUT', 'SERVICES', 'CONTACT', 'CLIENT']

  return (
    <main className='sidebar'>

    {menu.map((e, i)=>{
      e = e.toLowerCase()
      return (
        <Link href={e == 'home' ? `/` : `/${e}`} key={i} >
          <div className="menuItem" id={e} onClick={()=>{
            sa(e)
          }} >
            <a style={{
            color : active == e ? 'orange' : 'gray',
          }}>{e}</a>
          </div>
        </Link>
      )
    })}


  </main>
  )
}

export default SideBar
