import Link from 'next/link'
import { useState, useEffect } from 'react'
import './navbar.scss'
import {useStoreActions, useStoreState} from 'easy-peasy'

const Navbar = () => {


  const [active, setActive] = useState()
  const menu = ['HOME', 'ABOUT', 'SERVICES', 'CONTACT', 'CLIENT']



  useEffect(()=>{
    console.log(active);

  }, [])

  return (
    <>
        <main className='sidebar'>

          {menu.map((e, i)=>{
            return (
              <Link href={e == 'HOME' ? `/` : `/${e.toLowerCase()}`} key={i} >
              <div className="menuItem" onClick={()=>{
                setActive(e.toLowerCase())
              }} style={{
                backgroundColor : ()=>{
                  e.toLowerCase() == active ? 'orange' : 'none'
                },
              }}>
                <a>{e}</a>
              </div>
              </Link>
            )
          })}


        </main>
    </>
  )
}

export default Navbar
