import Link from 'next/link'
import { useState, useEffect } from 'react'
import './navbar.scss'
import {useStoreActions, useStoreState} from 'easy-peasy'

const Navbar = () => {


  const [active, setActive] = useState()
  const menu = ['HOME', 'ABOUT', 'SERVICES', 'CONTACT', 'CLIENT']

  const changeColor = (x)=>{
    const elem = document.getElementById(x)
    elem.style.backgroundColor = 'orange'
  }

  useEffect(()=>{
    console.log(active);



  }, [])

  return (
    <>
        <main className='sidebar'>

          {menu.map((e, i)=>{
            e = e.toLowerCase()
            return (
              <Link href={e == 'home' ? `/` : `/${e}`} key={i} >
              <div className="menuItem" onClick={()=>{
                setActive(e)
                changeColor(e)
              }} id={e}>
                <a>{e}</a>
              </div>
              </Link>
            )
          })}


        </main>
    </>
  )
}

export default Navbar
