import Link from 'next/link'
import { useState } from 'react'
import './navbar.scss'


const Navbar = () => {


  const [active, setActive] = useState('')
  const menu = ['HOME', 'ABOUT', 'SERVICES', 'CONTACT', 'CLIENT']
  return (
    <>
        <main className='sidebar'>

          {menu.map((e, i)=>{
            return (
              <Link href={e == 'HOME' ? `/` : `/${e.toLowerCase()}`} key={i} >
              <div className="menuItem" onClick={()=>{
                setActive(e.toLowerCase())
                console.log(active);

              }}>
                <a>{e}</a>

              </div>
              </Link>
            )
          })}


        </main>
    </>
  )
}

export default Navbar
